import React, { useEffect, useState } from 'react';
import { Table, Tag, Space, Button, Image } from 'antd';
import type { ColumnsType } from 'antd/es/table';
import { history, request, useRequest } from 'umi';
import { getStuList, stuDelete } from '@/api/stu';
import { bannerPage } from '@/api/banner';
interface DataType {
  id: string;
  name: string;
  age: number;
  gender: number;
  record: number;
  address: string;
}
// 区分性别
const reTagGender = (record: DataType) => {
  if (record.gender == 0) {
    return <Tag color="red">女</Tag>;
  } else {
    return <Tag color="blue">男</Tag>;
  }
};
// 区分是否及格
const reTagRecord = (record: DataType) => {
  if (record.record >= 60) {
    return <Tag color="blue">{record.record}</Tag>;
  } else {
    return <Tag color="red">{record.record}</Tag>;
  }
};

const BannerList = () => {
  const columns: ColumnsType<DataType> = [
    {
      title: '活动名称',
      dataIndex: 'bannerName',
      key: 'bannerName',
      render: (text) => <a>{text}</a>,
    },
    {
      title: '活动链接',
      dataIndex: 'bannerUrl',
      key: 'bannerUrl',
      render: (text) => <a href={text}>{text}</a>,
    },
    {
      title: '创建时间',
      dataIndex: 'createdAt',
      key: 'createdAt',
    },
    {
      title: '性别',
      key: 'gender',
      dataIndex: 'gender',
      render: (text, record, index) => {
        return reTagGender(record);
      },
    },
    {
      title: '活动图片',
      key: 'bannerLink',
      dataIndex: 'bannerLink',
      render: (text, record: any, index) => {
        return (
          <Image
            width={100}
            src={record.bannerLink}
            fallback="data:image/png;base64,"
          />
        );
      },
    },
    {
      title: '操作',
      key: 'operation',
      dataIndex: 'operation',
      render: (text, record, index) => {
        return (
          <Space size="small">
            <Button type="link" size="small" onClick={() => handleEdit(record)}>
              编辑
            </Button>
            <Button
              type="link"
              danger
              size="small"
              onClick={() => handleDelete(record)}
            >
              删除
            </Button>
          </Space>
        );
      },
    },
  ];
  const [list, setList] = useState([]);
  // const [loading, setLoading] = useState(false);
  const handleEdit = (record: DataType): void => {
    history.push({
      pathname: '/banner/edit',
      query: { ...record, imageUrl: record.bannerLink },
    });
  };
  const handleDelete = (record: DataType) => {
    console.log(record.id);
    stuDelete(record.id)
      .then((result) => {
        bannerPage().then((res) => {
          console.log(res);
          setList(res.list);
        });
      })
      .catch((err) => {});
  };

  useEffect(() => {
    // 方法一、需要开发者自行判断是否loading并赋值
    // setLoading(true);
    // getStuList().then((res) => {
    //   setLoading(false);
    //   console.log(res);
    //   setList(res.list);
    // });
  }, []);
  // // 方法二、用useRequest中的loading参数，简化异步请求
  let { data = [], loading, error } = useRequest(bannerPage);
  // data---后端响应的数据包
  // loading---异步请求状态
  // error---异步请求返回的错误信息
  console.log(data);
  // setList(data);
  return (
    <div>
      <Table
        loading={loading}
        rowKey={'objectId'}
        columns={columns}
        dataSource={data}
      />
    </div>
  );
};

export default BannerList;
